import{_ as e,o as a,c as t,a as r}from"./app.bfa9bd4b.js";const f=JSON.parse('{"title":"诗","description":"","frontmatter":{},"headers":[{"level":2,"title":"今日欢呼孙大圣，只缘妖雾又重来","slug":"今日欢呼孙大圣-只缘妖雾又重来","link":"#今日欢呼孙大圣-只缘妖雾又重来","children":[]},{"level":2,"title":"雄关漫道真如铁，而今迈步从头越","slug":"雄关漫道真如铁-而今迈步从头越","link":"#雄关漫道真如铁-而今迈步从头越","children":[]},{"level":2,"title":"遍地哀鸿满城血，无非一念救苍生","slug":"遍地哀鸿满城血-无非一念救苍生","link":"#遍地哀鸿满城血-无非一念救苍生","children":[]},{"level":2,"title":"今日长缨在手，何时缚住苍龙","slug":"今日长缨在手-何时缚住苍龙","link":"#今日长缨在手-何时缚住苍龙","children":[]}],"relativePath":"3.框架/问题解决/诗.md","lastUpdated":1720835436698}'),i={name:"3.框架/问题解决/诗.md"},d=r('<h1 id="诗" tabindex="-1">诗 <a class="header-anchor" href="#诗" aria-hidden="true">#</a></h1><h2 id="今日欢呼孙大圣-只缘妖雾又重来" tabindex="-1">今日欢呼孙大圣，只缘妖雾又重来 <a class="header-anchor" href="#今日欢呼孙大圣-只缘妖雾又重来" aria-hidden="true">#</a></h2><p>毛泽东《七律·和郭沫若同志》</p><p>一从大地起风雷，便有精生白骨堆。 僧是愚氓犹可训，妖为鬼蜮必成灾。 金猴奋起千钧棒，玉宇澄清万里埃。 今日欢呼孙大圣，只缘妖雾又重来。</p><h2 id="雄关漫道真如铁-而今迈步从头越" tabindex="-1">雄关漫道真如铁，而今迈步从头越 <a class="header-anchor" href="#雄关漫道真如铁-而今迈步从头越" aria-hidden="true">#</a></h2><p>毛泽东《忆秦娥·娄山关》</p><p>西风烈，长空雁叫霜晨月。</p><p>霜晨月，马蹄声碎，喇叭声咽。</p><p>雄关漫道真如铁，而今迈步从头越。</p><p>从头越，苍山如海，残阳如血。</p><h2 id="遍地哀鸿满城血-无非一念救苍生" tabindex="-1">遍地哀鸿满城血，无非一念救苍生 <a class="header-anchor" href="#遍地哀鸿满城血-无非一念救苍生" aria-hidden="true">#</a></h2><p>毛泽东《七律·忆重庆谈判》</p><p>有田有地皆吾主，无法无天是为民。 重庆有官皆墨吏，延安无土不黄金。 炸桥挖路为团结，夺地争城是斗争。 遍地哀鸿满城血，无非一念救苍生。</p><h2 id="今日长缨在手-何时缚住苍龙" tabindex="-1">今日长缨在手，何时缚住苍龙 <a class="header-anchor" href="#今日长缨在手-何时缚住苍龙" aria-hidden="true">#</a></h2><p>毛泽东《清平乐·六盘山》</p><p>天高云淡，望断南飞雁。</p><p>不到长城非好汉，屈指行程二万。</p><p>六盘山上高峰，红旗漫卷西风。</p><p>今日长缨在手，何时缚住苍龙？</p>',19),n=[d];function p(h,l,s,c,_,o){return a(),t("div",null,n)}const m=e(i,[["render",p]]);export{f as __pageData,m as default};
